import { useState } from 'react';
import { Link } from 'react-router-dom';

import '../css/Home.css';



const SearchBar = (props) => {
  const [searchText, setSearchText] = useState('');
  const [server, setServer] = useState('na');

  return (
    <div id='searchWrapper'>
      <img alt='search icon' className="search-icon" src="data:image/svg+xml;utf8;base64," />
      <input
        type='text'
        placeholder='Search'
        value={searchText}
        onChange={(e) => setSearchText(e.target.value)}
        id='searchTextArea'
      />
      <div id='searchControls'>
        <select
          id='serverDropdown'
          value={server}
          onChange={(e) => setServer(e.target.value)}
        >
          <option value='na'>NA</option>
          <option value='euw'>EUW</option>
          <option value='kr'>KR</option>
        </select>
        <Link
          id=''
          to={'/summoner/' + server + '/' + searchText}>
          <button
            id='searchButton'
          >
            Search
          </button>
        </Link>
      </div>
    </div>
  )
};

const AppInfo = (props) => {
  return (
    <div id="info">
      <h2>Instructions</h2>
      <p>Search a League of Legends players username to view playtime statistics.</p>
    </div>
  )
};

const Home = (props) => {

  return (
    <div id='content'>
      <h1 id='title'>League-Time</h1>
      <SearchBar/>
      <AppInfo/>
    </div>
  );
};

export default Home;